            return builder.ToString();
        }

        internal ChannelResponse(params ChannelItem[] channels) : base("channels", channels)
        {
        }

        public override XElement GetItem(ChannelItem item)
        {
            var xml = new XElement("item",
                new XElement("lastvalue", item.LastValue),
                new XElement("lastvalue_raw", item.LastValueRaw),
                new XElement("objid", item.ObjId),
                new XElement("objid_raw", item.ObjIdRaw),
                new XElement("name", item.Name),
                new XElement("type", item.Type)
            );

            return xml;
        }

        public override string GetResponseText(ref string address)
        {
            if (address.Contains(HtmlFunction.ChannelEdit.GetDescription()))
            {
                var components = UrlUtilities.CrackUrl(address);
                var channelId = Convert.ToInt32(components["channel"]);

                return GetSettingsResponseText(channelId);
            }

            return base.GetResponseText(ref address);
        }
    }
